import { Div, Image, THEME } from "./shared";

const SOCIALS = {
  Website: {
    href: "https://podter.me",
    logo: {
      light:
        "data:image/svg+xml;base64,",
      dark: "data:image/svg+xml;base64,",
    },
  },
  Discord: {
    href: "https://discord.com/users/331793642689789962",
    logo: {
      light: "discord",
      dark: "discord",
    },
  },
  Twitter: {
    href: "https://x.com/Real_Podter",
    logo: {
      light: "x",
      dark: "x",
    },
  },
  Email: {
    href: "mailto:hello@podter.me",
    logo: {
      light:
        "data:image/svg+xml;base64,",
      dark: "data:image/svg+xml;base64,",
    },
  },
};

export function Socials() {
  return (
    <Div align="center">
      {Object.entries(SOCIALS).map(([name, { href, logo }]) => {
        const base = new URL("/static/v1", "https://img.shields.io");
        base.searchParams.set("message", name);
        base.searchParams.set("style", "for-the-badge");
        base.searchParams.set("label", "");

        const light = new URL(base);
        light.searchParams.set("logo", logo.light);
        light.searchParams.set("color", THEME.light.background);
        light.searchParams.set("logoColor", THEME.light.text);

        const dark = new URL(base);
        dark.searchParams.set("logo", logo.dark);
        dark.searchParams.set("color", THEME.dark.background);
        dark.searchParams.set("logoColor", THEME.dark.text);

        return (
          <a href={href} target="_blank">
            <Image
              srcLight={light.toString()}
              srcDark={dark.toString()}
              alt={name}
            />
          </a>
        );
      })}
    </Div>
  );
}
